import React, {useEffect, useState} from 'react'
import Backdrop from '@mui/material/Backdrop';
import CircularProgress from '@mui/material/CircularProgress';
import msftinitialdata from '../static/msftinitialdata.png'
import msftlstmvsdata from '../static/msftlstmvsdata.png'
import micorsoft_logo from '../Images/microsoft_logo.png'

const Predictions = () => {
    const [data, setData] = useState([])

    const getData = async () =>{
        if(data.length === 0){
            const response  = await fetch(`http://127.0.0.1:5000/api/microsoft`);
            setData(await response.json());
            console.log(data);
        }
    }

    useEffect(()=> {
        getData();
    }, [getData]);

  return (
    <>
        <div className="google-value">
            <div className="predicted_price">
                <div className="predicted_price_header">Microsoft</div>
                <img src={micorsoft_logo} alt="msft" />
                <img src={msftinitialdata} alt="msft"/>
                <div className='msft_lstm_section'>
                    <p>Closing price predicted by LSTM model.</p>
                    <img src={msftlstmvsdata} alt="msft"/>
                    <p>Next seven day closing price</p>
                    <div className="value">{data?.msftlstmdata ? data?.msftlstmdata : <Backdrop
                        sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                        open>
                        <CircularProgress color="inherit" />
                        </Backdrop>}
                    </div>
                </div>

                <div className='msft_arima_section'>
                    <p>Closing price predicted by Arima model.</p>

                </div>
            </div>
        </div>
    </>
  )
}

export default Predictions
